import React from "react";
import maintenance from "../assets/maintenance.jpg";
import UserList from "../components/UserList";
import PostSubmit from "../components/PostSubmit";
import { useSelector } from "react-redux";
import PostFeed from "../components/PostFeed";
import logo from "../assets/logo.png";

const HomePage = () => {
  const { isLoggedIn } = useSelector((store) => ({
    isLoggedIn: store.isLoggedIn,
  }));

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-lg-2 d-none d-lg-block">
          <div className="card-body">
            <div className="card-header text-center">
              <img src={logo} width="65" alt="Hoaxify logo" />
              <a className="navbar-brand ms-2">Social Cafe </a>
            </div>
            <div className="card-body">
              <ul className="list-group">
                <li className="nav-item list-group-item-action">
                  <i className="fas fa-user me-2"></i> Profile
                </li>
                <li className="list-group-item">
                  <i class="fa-solid fa-user-group me-2"></i> Friends
                </li>
                <li className="list-group-item">
                  <i className="fas fa-envelope me-2"></i> Messages
                </li>
                <li className="list-group-item">
                  <i className="fas fa-bell me-2"></i> Notifications
                </li>
                <li className="list-group-item">
                  <i class="fa-solid fa-circle-check me-2"></i> Verify
                </li>
                <li className="list-group-item">
                  <i class="fa-solid fa-circle-info me-2"></i> Help
                </li>
                <li className="list-group-item">
                  <i className="fas fa-cog me-2"></i> Settings
                </li>
                {/* Add more buttons for other functionalities */}
              </ul>
            </div>
          </div>
        </div>
        <div className="col-lg-7 col-md-8 mb-3">
          <div className="mb-3">{isLoggedIn && <PostSubmit />}</div>
          <PostFeed />
        </div>
        <div className="col-lg-3 col-md-4 mb-3">
          <UserList />
        </div>
      </div>
    </div>
  );
};

export default HomePage;
