import React from "react";
import { signup } from '../api/apiCalls';
import Input from '../components/Input'
import { withTranslation } from "react-i18next";
import './18n';
class UserSignupPage extends React.Component{
    
    
    state = {
        username: null,
        displayName: null,
        password: null,
        passwordRepeat: null,
        pendingApiCall: false,
        errors:{
        },
        
    };

    onChange = event => {
        const { t } = this.props;
        const {name, value} = event.target;
        const errors = {...this.state.errors}
        errors[name] = undefined
        if(name === 'password' || name === 'passwordRepeat'){
            if(name === 'password' && value !== this.state.passwordRepeat){
                errors.passwordRepeat = t('Password mismatch');
            }
            else if(name=== 'passwordRepeat' && value !== this.state.password){
                errors.passwordRepeat = t('Password mismatch');
            }
            else{
                errors.passwordRepeat = undefined;
            }
        }
        this.setState({
            [name]: value,
            errors
        })
    };

    onClickSignup = async event => {
        event.preventDefault();
        const {username, displayName, password} = this.state;
        const body = {
            username,
            displayName,
            password
        };

        this.setState({pendingApiCall: true});
        try{
            const response = await signup(body);
        }catch(error){
            if(error.response.data.validationErrors){
                this.setState({errors: error.response.data.validationErrors});
            }
        }
        
        this.setState({pendingApiCall: false});

    };

    onChangeLanguage = language =>{
        const{i18n} = this.props;
        18n.changeLanguage(language);

    };

    render(){
        const {pendingApiCall, errors} = this.state;
        const {username, displayName,password, passwordRepeat} = errors;
        const { t } = this.props;
        return(
            <div className="container">
                <div className="d-flex flex-row-reverse">
                        <img src="https://flagsapi.com/US/flat/48.png" alt="USA Flag" onClick={() => this.onChangeLanguage('en')}/>
                        <img src="https://flagsapi.com/TR/flat/48.png" alt="Turkish Flag" onClick={() => this.onChangeLanguage('tr')}/>
                    </div>
                <form>
                    <h1 className="text-center mt-3">{t('Sign Up')}</h1>
                    <Input name="username" label={t("Username")} error={username} onChange={this.onChange}/>
                    <Input name="displayName" label={t("Display Name")} error={displayName} onChange={this.onChange}/>
                    <Input name="password" label={t("Password")} type="password" error={password} onChange={this.onChange}/>
                    <Input name="passwordRepeat" label={t("Password Repeat")} type="password" error={passwordRepeat} onChange={this.onChange}/>
                    <div className="text-center mt-3">
                        <button className="btn btn-primary" onClick={this.onClickSignup} disabled={pendingApiCall || passwordRepeat !== undefined}>
                        {pendingApiCall && <span className="spinner-border spinner-border-sm" role="status" aria-hidden="true"></span>}
                        {t('Sign Up')}
                        </button>
                    </div>
                </form>
                
            </div>
        )
    }
}

const UserSignupPageWithTranslation = withTranslation()(UserSignupPage);

export default UserSignupPageWithTranslation;
