import i18n from "i18next";
import { initReactI18next } from "react-i18next";
import fetchTranslationFromAPI from "../src/api/apiCalls"
import { register } from "timeago.js";

i18n.use(initReactI18next).init({
  resources: {},
  fallbackLng: "en",
  ns: ["translations"],
  defaultNS: "translations",
  keySeperator: false,
  interpolation: {
    escapeValue: false,
    formatSeperator: ",",
  },
  react: {
    wait: true,
  },
});

// Dil destekli metinleri çekme işlemi
async function loadTranslations(language) {
  const translationKeys = Object.keys(i18n.options.resources.en.translations); // Anahtar kelimeleri alın
  const translations = {};
  
  for (const key of translationKeys) {
    const text = await fetchTranslationFromAPI(key, language);
    translations[key] = text;
  }

  i18n.addResourceBundle(language, "translations", translations, true, true);
}

// Dil değişikliğinde metinleri güncelleme
i18n.on("languageChanged", (lng) => {
  loadTranslations(lng);
});

// Timeago dil desteği ekleme
const timeagoTR = (number, index) => {
  // ... zaman ifadeleri
};
register("tr", timeagoTR);

// Varsayılan olarak İngilizce metinleri yükle
loadTranslations("en");

export default i18n;
