import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { getPostDetails } from "../api/apiCalls";
import PostView from "../components/PostView";
import { useSelector } from "react-redux";
import Sidebar from "../components/Sidebar";
import Spinner from "../components/Spinner";
import { useTranslation } from "react-i18next";

const PostViewDetails = () => {
  const { postId } = useParams();
  const loggedInUser = useSelector((store) => store.username);
  const { t } = useTranslation();
  const [postDetails, setPostDetails] = useState();

  useEffect(() => {
    const getPost = async () => {
      try {
        const response = await getPostDetails(loggedInUser, postId);
        setPostDetails(response.data);
      } catch (error) {
        console.error("Error fetching post details:", error);
        setPostDetails(null);
      }
    };
    getPost();
  }, [postId]);

  if (!postDetails) {
    return (
      <div className="card my-2 text-center">
        <div className="alert alert-primary d-inline-block m-2" role="alert">
          <div>
            <Spinner />
          </div>
          <div className="ms-auto">{t("Loading...")}</div>
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-3 pb-1">
      <div className="row">
        <div className="col-lg-2">
          <Sidebar />
        </div>
        <div className="col-lg-8 col-md-7 mb-3">
          <PostView post={postDetails} onDeletePost={() => {}} />
          <div className="card" style={{ borderRadius: "0px" }}>
            <div className="card-body"  d-flex justify-content-between align-items-center>
              <textarea
                className="form-control"
                rows="4"
                placeholder="Write a comment..."
              />
              <button className="btn btn-post mt-2">{t("Submit Comment")}</button>
            </div>
          </div>
        </div>
        <div className="col-lg-2 col-md-5 mb-3">
          {/* Trendler Sütunu */}
          <div className="card">
            <div className="card-header">
              {t("İlginizi Çekebilecek Trendler")}
            </div>
            <ul className="list-group list-group-flush">
              <li className="list-group-item d-flex justify-content-between align-items-center">
                #Trend1
                <span className="badge badge-primary text-primary badge-pill">
                  14 Posts
                </span>
              </li>
              <li className="list-group-item d-flex justify-content-between align-items-center">
                #Trend2
                <span className="badge badge-primary text-primary badge-pill">
                  14 Posts
                </span>
              </li>
              <li className="list-group-item d-flex justify-content-between align-items-center">
                #Trend3
                <span className="badge badge-primary text-primary badge-pill">
                  14 Posts
                </span>
              </li>
              {/* Diğer trendler */}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PostViewDetails;
