import * as ACTIONS from "./Constants";
import { login, signup, logout,likePostApi,unlikePostApi,fetchLikedPosts } from "../api/apiCalls";

export const loginSuccess = (authState) => {
  return async (dispatch) => {
    dispatch({
      type: ACTIONS.LOGIN_SUCCES,
      payload: authState,
    });

    if (authState.isLoggedIn) {
      // Kullanıcı giriş yaptığında yapılması gereken işlemler
      const likedPosts = await fetchLikedPosts(authState.username);
      dispatch(updateLikedPosts(likedPosts));
    }
  };
};

export const logoutSucces = () => {
  return async (dispatch) => {
    await logout();
    dispatch({
      type: ACTIONS.LOGOUT_SUCCES,
    });
    dispatch(updateLikedPosts({}));
  };
};

export const updateLikedPosts = (likedPosts) => {
  return {
    type: ACTIONS.UPDATE_LIKED_POSTS,
    payload: likedPosts,
  };
};


export const updateSuccess = ({ displayName, image }) => {
  return {
    type: ACTIONS.UPDATE_SUCCESS,
    payload: {
      displayName,
      image,
    },
  };
};

export const updateAdminStatus = (admin) => ({
  type: ACTIONS.UPDATE_ADMIN_STATUS,
  payload: { admin },
});

export const loginHandler = (credentials) => {
  return async function (dispatch) {
    const response = await login(credentials);
    const authState = {
      ...response.data.user,
      password: credentials.password,
      token: response.data.token,
    };
    dispatch(loginSuccess(authState));
    dispatch(updateAdminStatus(response.data.user.admin));
    return response;
  };
};

export const signupHandler = (user) => {
  return async function (dispatch) {
    const response = await signup(user);
    await dispatch(loginHandler(user));
    return response;
  };
};
export const likePost = (postId, loggedInUsername) => {
  return async (dispatch) => {
    try {
      await likePostApi(postId, loggedInUsername);
      dispatch({ type: 'LIKE_POST', payload: postId });
      dispatch(updateLikedPosts(await fetchLikedPosts(loggedInUsername)));
    } catch (error) {
      console.error('Error liking post:', error);
    }
  };
};

export const unlikePost = (postId, loggedInUsername) => {
  return async (dispatch) => {
    try {
      await unlikePostApi(postId, loggedInUsername);
      dispatch({ type: 'UNLIKE_POST', payload: postId });
      dispatch(updateLikedPosts(await fetchLikedPosts(loggedInUsername)));
    } catch (error) {
      console.error('Error unliking post:', error);
    }
  };
};
