import React, { useEffect, useState } from 'react';
import { checkFollowStatusApi, followUser, unfollowUser } from '../api/apiCalls';
import { useTranslation } from "react-i18next";

const FollowButton = ({ loggedInUsername, username }) => {
  const { t } = useTranslation();
  const [isFollowing, setIsFollowing] = useState(false);

  useEffect(() => {
    const fetchFollowStatus = async () => {
      try {
        const response = await checkFollowStatusApi(loggedInUsername, username);
        setIsFollowing(response.data);
        console.log(isFollowing);
      } catch (error) {
        console.error("Takip durumu alınamadı:", error);
      }
    };
    fetchFollowStatus();
  }, [loggedInUsername, username]);

  const handleFollow = async () => {
    try {
      await followUser(loggedInUsername, username);
      setIsFollowing(true);
    } catch (error) {
      console.error("Takip etme hatası:", error);
    }
  };

  const handleUnfollow = async () => {
    try {
      await unfollowUser(loggedInUsername, username);
      setIsFollowing(false);
    } catch (error) {
      console.error("Takipten çıkma hatası:", error);
    }
  };

  return (
    <>
      {!isFollowing ? (
        <button className="btn btn-follow m-1" onClick={handleFollow}>
          {t("Follow")}
        </button>
      ) : (
        <button className="btn btn-unfollow m-1" onClick={handleUnfollow}>
           {t("Unfollow")}
        </button>
      )}
    </>
  );
};

export default FollowButton;
