import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { logoutSucces } from "../redux/authActions";
import ProfileImageWithDefault from "../components/ProfileImageWithDefault";
import { useTranslation } from "react-i18next";
import logo from "../assets/logo.png";
import { useParams, useHistory } from "react-router-dom";
import LanguageSelector from "./LanguageSelector";

const Sidebar = (props) => {
  const { displayName, isLoggedIn, image, username } = useSelector((store) => ({
    isLoggedIn: store.isLoggedIn,
    displayName: store.displayName,
    image: store.image,
    username: store.username,
  }));
  const [user, setUser] = useState({});
  useEffect(() => {
    setUser(props.user);
  }, [props.user]);
  const { admin } = user;
  const { t } = useTranslation();
  const dispatch = useDispatch();
  const onLogoutSuccess = () => {
    dispatch(logoutSucces());
  };
  const history = useHistory();
  const isAdmin = useSelector((store) => store.admin);

  const getPage = async () => {
    try {
      history.push("/verifications/applications");
    } catch (error) {
      console.error("Error getting verification requests:", error);
    }
  };
  return (
    <div className="position-sticky top-0 start-0 bottom-1 bg-white">
      {isLoggedIn && (
        <div className="card">
          <div className="card-body">
            <ul className="list-group">
              {/* <Link className="list-group-item text-center" to="/">
                <img src={logo} width="48" alt="Hoaxify logo" />
              </Link>
              <br></br> */}

              <li className="list-group-item list-group-item-action">
                <Link
                  to="/"
                  className="d-flex align-items-center text-dark"
                  style={{ textDecoration: "none" }}
                >
                  <i className="fas fa-home me-2"></i>
                  {t("Home")}
                </Link>
              </li>
              <li className="list-group-item list-group-item-action">
                <Link
                  to={"/users/" + username}
                  className="d-flex align-items-center text-dark"
                  style={{ textDecoration: "none" }}
                >
                  <i className="fas fa-user me-2"></i>
                  {t("Profile")}
                </Link>
              </li>

              <li
                className="list-group-item list-group-item-action"
                style={{ cursor: "pointer" }}
              >
                <i className="fas fa-magnifying-glass me-1"></i> {t("Explore")}
              </li>

              <br></br>

              <li
                className="list-group-item list-group-item-action"
                style={{ cursor: "pointer" }}
              >
                <i className="fas fa-bell me-2 text-dark"></i>{" "}
                {t("Notifications")}
              </li>
              <li
                className="list-group-item list-group-item-action"
                style={{ cursor: "pointer" }}
              >
                <i className="fas fa-envelope me-2 text-dark"></i>{" "}
                {t("Messages")}
              </li>
              <li
                className="list-group-item list-group-item-action"
                style={{ cursor: "pointer" }}
              >
                <i className="fas fa-user-group me-2"></i> {t("Friends")}
              </li>
              {admin && (
                <li
                  className="list-group-item list-group-item-action"
                  style={{ cursor: "pointer" }}
                >
                  <button
                    className="btn btn-info d-inline-flex"
                    onClick={getPage}
                  >
                    <i className="material-icons me-2">verified</i>
                    {t("Verified User Application")}
                  </button>{" "}
                  <i className="fas fa-circle-check me-2 text-dark"></i>{" "}
                  {t("Verified User Application")}
                </li>
              )}
              {admin && (
                <>
                  {" "}
                  <div className="pt-2">
                    <button
                      className="btn btn-info d-inline-flex"
                      onClick={getPage}
                    >
                      <i className="material-icons me-2">verified</i>
                      {t("Verified User Application")}
                    </button>{" "}
                  </div>
                </>
              )}
              <br></br>
              <li
                className="list-group-item list-group-item-action"
                style={{ cursor: "pointer" }}
              >
                <i className="fas fa-circle-info me-2 text-dark"></i>{" "}
                {t("Help")}
              </li>
              <li
                className="list-group-item list-group-item-action"
                style={{ cursor: "pointer" }}
              >
                <i className="fas fa-cog me-2"></i> {t("Settings")}
              </li>
              <li
                className="list-group-item list-group-item-action"
                onClick={onLogoutSuccess}
              >
                <Link
                  to="/login"
                  className="d-flex align-items-center text-dark"
                  style={{ textDecoration: "none" }}
                >
                  <i class="fa-solid fa-right-from-bracket me-2 text-dark"></i>
                  {t("Logout")}
                </Link>
              </li>
              <li className="list-group-item d-flex justify-content-center">
                <LanguageSelector />
              </li>
            </ul>
          </div>
        </div>
      )}
    </div>
  );
};

export default Sidebar;
