import React, { useState } from "react";
import { signup } from "../api/apiCalls";
import Input from "../components/Input";
import { withTranslation } from "react-i18next";
import ButtonWithProgress from "./ButtonWithProgress";
import { withApiProgress } from "../shared/ApiProgress";
import background from "../assets/45050925.jpg";
import { connect } from "react-redux";
import { signupHandler } from "../redux/authActions";

const UserSignupPage = (props) => {
  const [form, setForm] = useState({
    username: null,
    displayName: null,
    password: null,
    passwordRepeat: null
  });
  
  const [errors, setErrors] = useState({});
  const onChange = (event) => {
    const { t } = props;
    const { name, value } = event.target;
    const errorsCopy = { ...errors };
    errorsCopy[name] = undefined;
    if (name === "password" || name === "passwordRepeat") {
      if (name === "password" && value !== form.passwordRepeat) {
        errorsCopy.passwordRepeat = t("Password mismatch");
      } else if (name === "passwordRepeat" && value !== form.password) {
        errorsCopy.passwordRepeat = t("Password mismatch");
      } else {
        errorsCopy.passwordRepeat = undefined;
      }
    }
    setErrors(errorsCopy);
    const formCopy={...form}
    formCopy[name] = value;
    setForm(formCopy);

  };

  const onClickSignup = async (event) => {
    event.preventDefault();
    const { history, dispatch } = props;
    const { push } = history;
    const { username, displayName, password } = form;
    const body = {
      username,
      displayName,
      password,
    };

    try {
      const response = await dispatch(signupHandler(body));
      push("/");
    } catch (error) {
      if (error.response.data.validationErrors) {
        setErrors(error.response.data.validationErrors);
      }
    }
  };

  const {
    username: usernameError,
    displayName: displayNameError,
    password: passwordError,
    passwordRepeat: passwordRepeatError,
  } = errors;
  const { t, pendingApiCall } = props;
  return (
    <div
      style={{
        backgroundImage: `url(${background})`,
        backgroundSize: "100%",
        backgroundPosition: "center center",
        height: "85vh",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        width: "100%",
        backgroundColor: "rgba(192, 192, 192, 1)",
      }}
    >
      <div
        className="container"
        style={{
          display: "block",
          alignItems: "center",
          justifyContent: "center",
          backgroundColor: "rgba(255, 255, 255, 0.8)",
          padding: "30px",
          borderRadius: "20px",
          width: "500px",
          height: "500px",
        }}
      >
        <form>
          <h1 className="text-center mt-3">{t("Sign Up")}</h1>
          <Input
            name="username"
            label={t("Username")}
            error={usernameError}
            onChange={onChange}
          />
          <Input
            name="displayName"
            label={t("Display Name")}
            error={displayNameError}
            onChange={onChange}
          />
          <Input
            name="password"
            label={t("Password")}
            type="password"
            error={passwordError}
            onChange={onChange}
          />
          <Input
            name="passwordRepeat"
            label={t("Password Repeat")}
            type="password"
            error={passwordRepeatError}
            onChange={onChange}
          />
          <div className="text-center mt-3">
            <ButtonWithProgress
              onClick={onClickSignup}
              disabled={pendingApiCall || form.passwordRepeat !== undefined}
              pendingApiCall={pendingApiCall}
              text={t("Sign Up")}
            />
          </div>
        </form>
      </div>
    </div>
  );
};
const UserSignupPageWithApiProgressForSignupRequest = withApiProgress(
  UserSignupPage,
  "/api/1.0/users"
);
const UserSignupPageWithApiProgressForAuthRequest = withApiProgress(
  UserSignupPageWithApiProgressForSignupRequest,
  "/api/1.0/auth"
);
const UserSignupPageWithTranslation = withTranslation()(
  UserSignupPageWithApiProgressForAuthRequest
);

export default connect()(UserSignupPageWithTranslation);
