import React, { useEffect, useState } from "react";
import { getLikedPosts } from "../api/apiCalls";
import PostView from "./PostView";
import Spinner from "../components/Spinner";
import { useParams } from "react-router-dom";
import { useTranslation } from "react-i18next";
import { useApiProgress } from "../shared/ApiProgress";
const LikedPostFeed = ({ username }) => {
  const [postPage, setPostPage] = useState({
    content: [],
    last: true,
    number: 0,
  });
  const {t} = useTranslation();
  const [newPostCount, setNewPostCount] = useState(0);
  useEffect(() => {
    console.log("API Response:", postPage); // API yanıtını postPage durumu ile kontrol edin
  }, [postPage]);
  
  useEffect(() => {
    const loadLikedPosts = async () => {
      try {
        const response = await getLikedPosts(username);
        console.log("API Response:", response.data); // API yanıtını konsola yazdır
        setPostPage((previousPostPage) => ({
          ...response.data,
          content: [...previousPostPage.content, ...response.data.content],
        }));
      } catch (error) {
        console.error("Error loading liked posts:", error);
      }
    };
    loadLikedPosts();
  }, [username]);
  

  const { content, last } = postPage;

  return (
    <div>
      {content.length > 0 ? (
        content.map((post) => (
          <div key={post.id} className="mb-">
            <PostView post={post}></PostView>
          </div>
        ))
      ) : (
        <b>notthing</b>
      )}
    </div>
  );
  
};

export default LikedPostFeed;
