import React from "react";
import LanguageSelector from "../components/LanguageSelector";
import logo from "../assets/logo.png";
import { Link } from "react-router-dom";
import { useTranslation } from "react-i18next";
import { useDispatch, useSelector } from "react-redux";
import { logoutSucces } from "../redux/authActions";
import ProfileImageWithDefault from "../components/ProfileImageWithDefault";

const TopBar = () => {
  const { t } = useTranslation();

  const { username, isLoggedIn, displayName, image } = useSelector((store) => ({
    isLoggedIn: store.isLoggedIn,
    username: store.username,
    displayName: store.displayName,
    image: store.image,
  }));

  const dispatch = useDispatch();

  const onLogoutSuccess = () => {
    dispatch(logoutSucces());
  };

  return (
    <div className="shadow-sm bg-light">
      <nav className="navbar navbar-expand-lg navbar-light container">
        <Link className="navbar-brand d-flex align-items-center" to="/">
          <img src={logo} width="48" alt="Hoaxify logo" />
          <span className="ms-2">Social Cafe</span>
        </Link>
        <em className="navbar-text ms-auto">
          {t("Share your coffee, explore the world!")}
        </em>
        <div className="collapse navbar-collapse justify-content-end">
          <ul className="navbar-nav">
            <li className="nav-item">
              <Link className="nav-link" to="/">
                <i className="fas fa-house me-1"></i>
                {t("Home Page")}
              </Link>
            </li>
            {isLoggedIn ? (
              <>
                <li className="nav-item">
                  <Link className="nav-link d-flex align-items-center" to={"/users/" + username}>
                    <ProfileImageWithDefault
                      image={image}
                      width="24"
                      height="24"
                      className="rounded-circle me-1"
                    />
                    {displayName}
                  </Link>
                </li>
                <li className="nav-item">
                  <Link className="nav-link d-flex align-items-center" to={"/users/" + username}>
                    <i className="fas fa-person me-1"></i>
                    {t("My Profile")}
                  </Link>
                </li>
                <li className="nav-item">
                  <Link className="nav-link d-flex align-items-center" to="/login" onClick={onLogoutSuccess}>
                    <i className="fas fa-logout me-1"></i>
                    {t("Logout")}
                  </Link>
                </li>
              </>
            ) : (
              <>
                <li className="nav-item">
                  <Link className="nav-link" to="/login">
                    <i className="fas fa-login me-1"></i>
                    {t("Login")}
                  </Link>
                </li>
                <li className="nav-item">
                  <Link className="nav-link" to="/signup">
                    <i className="fas fa-user-plus me-1"></i>
                    {t("Sign Up")}
                  </Link>
                </li>
              </>
            )}
            <li className="nav-item">
              <LanguageSelector />
            </li>
          </ul>
        </div>
      </nav>
    </div>
  );
};

export default TopBar;
