import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getPostDetails } from '../api/apiCalls';
import PostView from '../components/PostView'; // PostView eklenmiş

const PostViewDetails = () => {
  const { postId } = useParams();
  const [postDetails, setPostDetails] = useState(null);

  useEffect(() => {
    const getPost = async () => {
      try {
        const response = await getPostDetails(postId);
        setPostDetails(response);
      } catch (error) {
        console.error('Error fetching post details:', error);
      }
    };
    getPost();
  }, [postId]);

  if (!postDetails) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>Post Details</h2>
      <PostView post={postDetails} onDeletePost={() => {}} /> {/* PostView kullanılıyor */}
    </div>
  );
};

export default PostViewDetails;
