import React from "react";
import ProfileImageWithDefault from "./ProfileImageWithDefault";
import { Link } from "react-router-dom";
import { format } from "timeago.js";
import { useTranslation } from "react-i18next";

const PostView = (props) => {
  const { post } = props;
  const { user, content, timestamp,fileAttachment } = post;
  const { username, displayName, image  } = user;

  const { i18n } = useTranslation();

  const formatted = format(timestamp, i18n.language);
  return (
    <div className="card p-1">
      <div className="d-flex">
        <ProfileImageWithDefault
          Link={`/users/${username}`}
          image={image}
          width="32"
          height="32"
          className="rounded-circle m-1"
        />
        <div className="flex-fill m-auto px-2">
          <Link
            to={`/users/${username}`}
            className="text-dark"
            style={{ textDecoration: "none" }}
          >
            <h6 className="d-inline">
              <span>{displayName}</span>
              <span className="text-muted"> @{username}</span>
            </h6>
            <span> Â· </span>
            <span>{formatted}</span>
          </Link>
        </div>
      </div>
      <div className="px-5 py-2"> {content} </div>
      {fileAttachment && (
        <div className="ps-5">
          <img className="img-fluid img-thumbnail rounded-4" style={{maxWidth: "5300px", maxHeight: "500px"}} src={"images/" + fileAttachment.name} alt={content} />
        </div>
      )}
      <hr class="text-black-50 my-1" />
      <div className="d-flex justify-content-around pb-1 py-1">
        <i class="fa-regular fa-comment"></i>
        <i class="fa-regular fa-window-restore"></i>
        <i class="fa-regular fa-heart"></i>
        <i class="fa-regular fa-bookmark"></i>
        <i class="fa-regular fa-share-from-square"></i>
      </div>
    </div>
  );
};

export default PostView;
