import React from "react";
import { useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { useTranslation } from "react-i18next";

const ProtectedFileViewer = () => {
  const { filename } = useParams();
  const { t } = useTranslation();
  const isAdmin = useSelector((store) => store.admin);

  if (!isAdmin) {
    return <div>{t("You are not authorized to view this file.")}</div>;
  }

  const protectedImageUrl = `/api/1.0/images/verified/${filename}`;

  return (
    <div>
      <h2>{t("Protected File Viewer")}</h2>
      <img src={protectedImageUrl} alt={filename} style={{ maxWidth: "100%" }} />
    </div>
  );
};

export default ProtectedFileViewer;
