import React, { Component } from "react";
import Input from "../components/Input";
class loginPage extends Component {
  state = {
    username: null,
    password: null,
    pendingApiCall: false,
    errors: {},
  };

  onChange = (event) => {
    const { name, value } = event.target;
    this.setState({
      [name]: value,
    });
  };

  render() {
    return (
      <div className="container" style={{backgroundColor: "rgba(128, 0, 128, 0.8)", // Saydam mor arka plan
    }}>
        <div className="d-flex flex-row-reverse">
          <img
            src="https://flagsapi.com/US/flat/48.png"
            alt="USA Flag"
            onClick={() => this.onChangeLanguage("en")}
            style={{ cursor: "pointer" }}
          />
          <img
            src="https://flagsapi.com/TR/flat/48.png"
            alt="Turkish Flag"
            onClick={() => this.onChangeLanguage("tr")}
            style={{ cursor: "pointer" }}
          />
        </div>
        <form>
          <h1 className="text-center mt-3">Login</h1>
          <Input label="Username" name="username" onChange={this.onChange} />
          <Input
            label="Password"
            name="password"
            type="password"
            onChange={this.onChange}
          />
          <div className="text-center mt-3">
            <button className="btn btn-success">
              Login
              <span
                className="spinner-border spinner-border-sm"
                role="status"
                aria-hidden="true"
              ></span>
            </button>
          </div>
        </form>
      </div>
    );
  }
}

export default loginPage;
