import React, { useState } from "react";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { format } from "timeago.js";
import { useTranslation } from "react-i18next";
import { likePost, unlikePost } from "../api/apiCalls";
import ProfileImageWithDefault from "./ProfileImageWithDefault";
import Modal from "../components/Modal";
import VerifiedBadge from "./VerifiedBadge";
import { deletePost, getOldPosts } from "../api/apiCalls";
import { useApiProgress } from "../shared/ApiProgress";

const PostView = (props) => {
  const loggedInUser = useSelector((store) => store.username);

  const { post, onDeletePost, onUpdatePost } = props;
  const { user, content, timestamp, fileAttachment, id, likes } = post;
  const { username, displayName, image, admin, verified } = user;

  const [modalVisible, setModalVisible] = useState(false);
  const [likeInProgress, setLikeInProgress] = useState(false);
  const [liked, setLiked] = useState(
    post.likes.some((like) => like.user.username === loggedInUser)
  );
  const likeCount = likes ? likes.length : 0;

  const { i18n, t } = useTranslation();

  const pendingApiCall = useApiProgress("delete", `/api/1.0/posts/${id}`, true);

  const onClickDelete = async () => {
    await deletePost(id);
    onDeletePost(id);
  };

  const onClickCancel = () => {
    setModalVisible(false);
  };

  const handleLike = async () => {
    if (likeInProgress) {
      return;
    }
    setLikeInProgress(true);
    try {
      if (liked) {
        await unlikePost(id, loggedInUser);
      } else {
        await likePost(id, loggedInUser);
      }
      const updatedPost = await getOldPosts(id, username);
      onUpdatePost(updatedPost);
      setLiked(!liked); // Durumu işlem tamamlandıktan sonra güncelle
    } catch (error) {
      console.error("Error while liking/unliking the post:", error);
    }
    setLikeInProgress(false);
  };

  const formatted = format(timestamp, i18n.language);
  const isAdmin = useSelector((store) => store.admin);
  const ownedByLoggedInUser = loggedInUser === username || isAdmin;

  return (
    <div className="card p-1">
      <div className="d-flex">
        <Link to={`/users/${username}`}>
          <ProfileImageWithDefault
            image={image}
            width="32"
            height="32"
            className="rounded-circle m-1"
          />
        </Link>
        <div className="flex-fill m-auto px-2">
          <Link
            to={`/users/${username}`}
            className="text-dark"
            style={{ textDecoration: "none" }}
          >
            <h6 className="d-inline">
              <span>{displayName}</span>
              <span className="text-muted"> @{username}</span>
            </h6>
          </Link>
          {verified && <VerifiedBadge isAdmin={admin} />}
          <span className="mx-2"> · </span>
          <span>{formatted}</span>
        </div>
        <div className="dropdown">
          <button
            className="btn btn-delete-link dropdown"
            type="button"
            data-bs-toggle="dropdown"
            aria-expanded="false"
          >
            <i className="fa-solid fa-ellipsis"></i>
          </button>
          <ul className="dropdown-menu">
            {ownedByLoggedInUser ? (
              <li>
                <button
                  className="dropdown-item btn btn-delete-link"
                  onClick={() => setModalVisible(true)}
                >
                  <i className="fa-regular fa-trash-can me-2"></i>
                  {t("Delete Post")}
                </button>
              </li>
            ) : (
              <li>
                <button className="dropdown-item btn btn-delete-link">
                  <i className="fa-solid fa-mug-hot me-2"></i>
                  {t("Social Cafe")}
                </button>
              </li>
            )}
          </ul>
        </div>
      </div>
      <div className="px-5 py-2">{content}</div>
      {fileAttachment && (
        <div className="ps-5">
          {fileAttachment.fileType.startsWith("image") ? (
            <img
              className="img-fluid img-thumbnail rounded"
              style={{ maxWidth: "100%", maxHeight: "450px" }}
              src={"images/attachment/" + fileAttachment.name}
              alt={content}
            />
          ) : (
            <strong>
              <em>Post has unkown attachment</em>
            </strong>
          )}
        </div>
      )}
      <hr className="text-black-50 my-1" />
      <div className="d-flex justify-content-around pb-1 py-1">
        <button className="btn btn-comment-link">
          <i className="fa-regular fa-comment"></i>
        </button>
        <button className="btn btn-retweet-link">
          <i className="fa-solid fa-retweet"></i>
        </button>
        <button
          className={`btn btn-fav-link ${liked ? "btn-fav-link-liked" : ""}`}
          onClick={handleLike}
        >
          <i className={`fa${liked ? "-solid" : "-regular"} fa-heart`}></i>
          <span className="ms-1">{likeCount} </span>
        </button>
        <button className="btn btn-bookmark-link">
          <i className="fa-regular fa-bookmark"></i>
        </button>
        <button className="btn btn-share-link">
          <i className="fa-regular fa-share-from-square"></i>
        </button>
      </div>
      <Modal
        visible={modalVisible}
        title={t("Delete Post")}
        onClickCancel={onClickCancel}
        cancelButtonText={t("Cancel")}
        onClickOk={onClickDelete}
        message={
          <div>
            <div>
              <strong>{t("Are you sure to delete Post?")}</strong>
            </div>
            <span>{content}</span>
          </div>
        }
        pendingApiCall={pendingApiCall}
        okButton={t("Delete Post")}
      />
    </div>
  );
};

export default PostView;
