import React,{ useEffect,useState } from "react";
import { getTrends } from "../api/apiCalls";
const [trends, setTrends] = useState([]);

  useEffect(() => {
    getTrends()
      .then(response => {
        setTrends(response.data); 
        console.log(response.data);
      })
      .catch(error => {
        console.error("Error fetching trends:", error);
      });
  }, []); 
const TrendList = ({  t }) => {
  if (!trends || trends.length === 0) {
    return null; 
  }

  return (
    <div className="card">
      <div className="card-header">{t("Trends for you")}</div>
      <ul className="list-group list-group-flush" style={{ cursor: "pointer" }}>
        {trends.map((trend, index) => (
          <li
            key={index}
            className="list-group-item list-group-item-action d-flex justify-content-between align-items-center"
          >
            #{trend.name}
            <span className="badge badge-primary text-primary badge-pill">
              {trend.postCount} {t("Posts")}
            </span>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TrendList;
