import React, { useEffect, useState } from "react";
import { getPosts } from "../api/apiCalls";
import { useTranslation } from "react-i18next";
import PostView from "./PostView";
import {useApiProgress} from "../shared/ApiProgress";
import Spinner from "../components/Spinner"

const PostFeed = () => {
  const [postPage, setPostPage] = useState({
    content: [],
    last: true,
    number: 0,
  });

  const { t } = useTranslation();

  useEffect(() => {
    loadPosts();
  }, []);

  const pendingApiCall = useApiProgress("get", "/api/1.0/posts");

  const loadPosts = async (page) => {
    try {
      const response = await getPosts(page);
      setPostPage((previousPostPage) => ({
        ...response.data,
        content: [...previousPostPage.content, ...response.data.content],
      }));
    } catch (error) {}
  };

  const { content, last, number } = postPage;

  if (content.length === 0) {
    return (
      <div className="alert alert-secondary text-center">
        {t("There are no posts")}
      </div>
    );
  }

  return (
    <div>
      {content.map((post) => {
        return <PostView key={post.id} post={post}></PostView>;
      })}
      {!last && (
        <div
          className="alert alert-secondary text-center"
          onClick={() => loadPosts(number + 1)}
          style={{ cursor: "pointer" }}
        >
          {pendingApiCall ? <Spinner/> : t("Load old posts")}
        </div>
      )}
    </div>
  );
};

export default PostFeed;
