import React from "react";
import { useState, useEffect } from "react";

const Footer = () => {
  const currentYear = new Date().getFullYear();

  const [showFooter, setShowFooter] = useState(true);

  useEffect(() => {
    const timeout = setTimeout(() => {
      setShowFooter(false);
    }, 5000); // Footer'ın 5 saniye sonra kaybolması için

    return () => clearTimeout(timeout);
  }, []);

  return (
    <>
      {showFooter && (
        <footer
          className="footer mt-auto py-3 bg-light fixed-bottom"
          style={{ transition: "opacity 1s" }}
        >
          <div className="container d-flex justify-content-center">
            <span className="text-muted">
              © {currentYear} SocialCoffee coded by{" "}
              <a
                href="https://www.linkedin.com/in/ferhatakalan"
                target="_blank"
                rel="noopener noreferrer"
                className="text-decoration-none"
              >
                Ferhat Akalan
              </a>{" "}
              <a
                href="https://github.com/FerhatAkalan"
                target="_blank"
                rel="noopener noreferrer"
                className="text-decoration-none"
              >
                Github
              </a>
            </span>
          </div>
        </footer>
      )}
    </>
  );
};

export default Footer;
