import React, { useEffect } from "react";
import i18n from "i18next";
import { initReactI18next } from "react-i18next";
import { register } from "timeago.js";
import { fetchTranslationFromAPI } from "../src/api/apiCalls"; // fetchTranslationFromAPI'yi import etmeyi unutmayın

i18n.use(initReactI18next).init({
  resources: {}, // Boş nesne olarak başlatıldı
  fallbackLng: "en",
  ns: ["translations"],
  defaultNS: "translations",
  keySeperator: false,
  interpolation: {
    escapeValue: false,
    formatSeperator: ",",
  },
  react: {
    wait: true,
  },
});

const timeagoTR = (number, index) => {
  // ... zaman ifadeleri
};
register("tr", timeagoTR);

function I18n() {
  useEffect(() => {
    loadTranslations(i18n.language);
  }, []);

  async function loadTranslations(language) {
    const translations = await fetchTranslationFromAPI(language);
    i18n.addResourceBundle(language, "translations", translations, true, true);
  }

  return (
    <div>
      {/* Diğer bileşen kodları */}
    </div>
  );
}

export default I18n;
