import React, { useEffect, useState } from "react";
import { useTranslation } from "react-i18next";
import {
  getVerificationRequests,
  approveVerificationRequest,
  rejectVerificationRequest,
  unVerifyUser,
} from "../api/apiCalls";
import Sidebar from "../components/Sidebar";
import Modal from "../components/Modal";
import { getProtectedImage } from "../api/apiCalls";

const VerificationRequestsPage = () => {
  const [verificationRequests, setVerificationRequests] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const { t } = useTranslation();
  const [selectedRequest, setSelectedRequest] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [showFileModal, setShowFileModal] = useState(false);
  const [selectedFile, setSelectedFile] = useState(null);
  const [fileData, setFileData] = useState(null);

  const showDetails = (request) => {
    setSelectedRequest(request);
    setShowModal(true);
  };

  const hideDetails = () => {
    setShowModal(false);
    setSelectedRequest(null);
  };

  const showFile = (fileUrl) => {
    setSelectedFile(fileUrl);
    handleFileClick(fileUrl); // Dosyayı alıp modalı açan fonksiyonu çağır
  };

  const hideFileModal = () => {
    setFileData(null);
    setSelectedFile(null);
    setShowFileModal(false);
  };

  const handleFileClick = async (fileUrl) => {
    try {
      const response = await getProtectedImage(fileUrl);
      console.log(response.data);
      setFileData(response.data);
      setShowFileModal(true);
    } catch (error) {
      // Hata durumlarıyla ilgili işlemler
      console.error("Dosya alınırken hata oluştu:", error);
    }
  };

  useEffect(() => {
    loadVerificationRequests();
  }, []);

  const loadVerificationRequests = async () => {
    setLoading(true);
    try {
      const response = await getVerificationRequests();
      setVerificationRequests(response.data);
      setError(null);
    } catch (error) {
      setError(error.message || t("Verification istekleri yüklenirken hata oluştu."));
    } finally {
      setLoading(false);
    }
  };

  const handleApprove = async (requestId) => {
    setLoading(true);
    try {
      await approveVerificationRequest(requestId);
      setError(null);
      loadVerificationRequests();
    } catch (error) {
      setError(error.message || t("Doğrulama isteği onaylanırken hata oluştu."));
    } finally {
      setLoading(false);
    }
  };

  const handleReject = async (requestId) => {
    setLoading(true);
    try {
      await rejectVerificationRequest(requestId);
      setError(null);
      loadVerificationRequests();
    } catch (error) {
      setError(error.message || t("Doğrulama isteği reddedilirken hata oluştu."));
    } finally {
      setLoading(false);
    }
  };

  const handleUnverify = async (userId) => {
    setLoading(true);
    try {
      await unVerifyUser(userId);
      setError(null);
      loadVerificationRequests();
    } catch (error) {
      setError(error.message || t("Kullanıcının doğrulaması kaldırılırken hata oluştu."));
    } finally {
      setLoading(false);
    }
  };
  
  
  return (
    <div className="container">
      <div className="row">
        <div className="col-lg-2 col-md-2">
          <Sidebar />
        </div>
        <div className="col-lg-10 col-md-10">
          <h2 className="m-2">{t("Onaylanmamış Kullanıcı Başvuruları")}</h2>
          {loading && <div className="text-center">{t("Yükleniyor...")}</div>}
          {error && <div className="text-center text-danger">{error}</div>}
          {!loading && !error && (
            <table className="table table-striped">
              <thead>
                <tr>
                  <th>{t("İstek ID")}</th>
                  <th>{t("Kullanıcı ID")}</th>
                  <th>{t("Kullanıcı Adı")}</th>
                  <th>{t("Neden")}</th>
                  <th>{t("Ek")}</th>
                  <th>{t("Durum")}</th>
                  <th>{t("İşlemler")}</th>
                </tr>
              </thead>
              <tbody>
                {verificationRequests.map((request) => (
                  <tr key={request.id}>
                    <td>{request.id}</td>
                    <td>{request.userId}</td>
                    <td>
                      <a
                        href={`/#/kullanicilar/${request.username}`}
                        target="_blank"
                        rel="noopener noreferrer"
                        style={{ textDecoration: "none" }}
                      >
                        {request.username}
                      </a>
                    </td>
                    <td>
                      {request.reason.length > 30
                        ? `${request.reason.slice(0, 30)}...`
                        : request.reason}
                    </td>
                    <td>
                      {request.attachment ? (
                        <button
                          className="btn btn-link"
                          onClick={() => showFile(`${request.attachment}`)}
                        >
                          {t("Ek Görüntüle")}
                        </button>
                      ) : (
                        t("Ek Yok")
                      )}
                    </td>
                    <td>
                      {request.verified ? (
                        <span className="text-success">{t("Doğrulandı")}</span>
                      ) : (
                        <span className="text-warning">{t("Doğrulanmadı")}</span>
                      )}
                    </td>
                    <td>
                      {request.verified ? (
                        <button
                          className="btn btn-warning me-2"
                          onClick={() => handleUnverify(request.userId)}
                        >
                          {t("Doğrulamayı Kaldır")}
                        </button>
                      ) : (
                        <>
                          <button
                            className="btn btn-success me-2"
                            onClick={() => handleApprove(request.id)}
                          >
                            {t("Onayla")}
                          </button>
                          <button
                            className="btn btn-danger"
                            onClick={() => handleReject(request.id)}
                          >
                            {t("Reddet")}
                          </button>
                        </>
                      )}
                      <button
                        className="btn btn-info ms-2"
                        onClick={() => showDetails(request)}
                      >
                        {t("Detaylar")}
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      </div>
      <Modal
        visible={showModal}
        onClickCancel={hideDetails}
        message={selectedRequest && selectedRequest.reason}
        onClickOk={hideDetails}
        pendingApiCall={false}
        title={t("Başvuru Detayları")}
        okButton={t("Tamam")}
      />

      <Modal
        visible={showFileModal}
        onClickCancel={hideFileModal}
        onClickOk={hideFileModal}
        pendingApiCall={false}
        title={selectedFile ? t("Ek Görüntüle") : ""}
      >
        {fileData && (
          <iframe
            src={`data:application/pdf;base64,${arrayBufferToBase64(fileData)}`}
            title="PDF Görüntüleyici"
            style={{ width: "100%", height: "800px" }}
          />
        )}
      </Modal>
    </div>
  );
};

export default VerificationRequestsPage;
