import React, { useState } from 'react';
import { Button, Modal } from 'react-bootstrap';

const VerifiedBadge = () => {
  const [modalVisible, setModalVisible] = useState(false);

  const openModal = () => {
    setModalVisible(true);
  };

  const closeModal = () => {
    setModalVisible(false);
  };

  return (
    <>
      <span
        className="d-inline-block"
        tabIndex="0"
        onClick={openModal}
        style={{ cursor: 'pointer' }}
      >
        <i className="material-icons text-info align-middle ms-2">
          verified
        </i>
      </span>

      <Modal show={modalVisible} onHide={closeModal} centered>
        <Modal.Header closeButton>
          <Modal.Title id="exampleModalCenterTitle">Onaylanmış Kullanıcı</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          Kullanıcı hesabı onaylandı.
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={closeModal}>
            Kapat
          </Button>
          <Button variant="primary" onClick={closeModal}>
            Kaydet
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default VerifiedBadge;
