import React, { useEffect, useState } from "react";
import { useTranslation } from "react-i18next";
import {
  getVerificationRequests,
  approveVerificationRequest,
  rejectVerificationRequest, unVerifyUser
} from "../api/apiCalls";
import Sidebar from "../components/Sidebar";
import { Link } from "react-router-dom";

const VerificationRequestsPage = () => {
  const [verificationRequests, setVerificationRequests] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const { t } = useTranslation();

  useEffect(() => {
    loadVerificationRequests();
  }, []);

  const loadVerificationRequests = async () => {
    setLoading(true);
    try {
      const response = await getVerificationRequests();
      setVerificationRequests(response.data);
      console.log(response.data); 
      setError(null);
    } catch (error) {
      setError(error.message || t("Error loading verification requests."));
    } finally {
      setLoading(false);
    }
  };

  const handleApprove = async (requestId) => {
    setLoading(true);
    try {
      await approveVerificationRequest(requestId);
      setError(null);
      loadVerificationRequests();
    } catch (error) {
      setError(error.message || t("Error approving verification request."));
    } finally {
      setLoading(false);
    }
  };

  const handleReject = async (requestId) => {
    setLoading(true);
    try {
      await rejectVerificationRequest(requestId);
      setError(null);
      loadVerificationRequests();
    } catch (error) {
      setError(error.message || t("Error rejecting verification request."));
    } finally {
      setLoading(false);
    }
  };
  const handleUnverify = async (requestId) => {
    setLoading(true);
    try {
      // Kullanıcının onay durumunu false yap
      await unVerifyUser(requestId); // Backend'de uygun API çağrısını yapmanız gerekecek
      setError(null);
      loadVerificationRequests();
    } catch (error) {
      setError(error.message || t("Error unverifying user."));
    } finally {
      setLoading(false);
    }
  };
  return (
    <div className="container">
      <div className="row">
        <div className="col-lg-2 col-md-2">
          <Sidebar />
        </div>
        <div className="col-lg-10 col-md-10">
          <h2 className="m-2">{t("Unverified User Applications")}</h2>
          {loading && <div className="text-center">{t("Loading...")}</div>}
          {error && <div className="text-center text-danger">{error}</div>}
          {!loading && !error && (
            <table className="table table-striped">
              <thead>
                <tr>
                  <th>{t("Request ID")}</th>
                  <th>{t("Username")}</th>
                  <th>{t("Reason")}</th> {/* Reason sütunu geri ekleniyor */}
                  <th>{t("Attachment")}</th>
                  <th>{t("Actions")}</th>
                </tr>
              </thead>
              <tbody>
                {verificationRequests.map((request) => (
                  <tr key={request.id}>
                    <td>{request.id}</td>
                    <td>
                      {" "}
                      <Link
                        style={{ textDecoration: "none" }}
                        to={`/users/${request.username}`}
                      >
                        {request.username}
                      </Link>
                    </td>
                    <td>{request.reason}</td> {/* Reason sütunu geri ekleniyor */}
                    <td>
                      {request.attachment ? (
                        <a
                          href={`images/verified/${request.attachment}`}
                          target="_blank"
                          rel="noopener noreferrer"
                        >
                          {t("View Attachment")}
                        </a>
                      ) : (
                        t("No Attachment")
                      )}
                    </td>
                    <td>
                      {request.isVerified ? (
                        <button
                          className="btn btn-warning me-2"
                          onClick={() => handleUnverify(request.id)}
                        >
                          {t("Unverify")}
                        </button>
                      ) : (
                        // Kullanıcı onaylanmamışsa, "Approve" düğmesini göster
                        <button
                          className="btn btn-success me-2"
                          onClick={() => handleApprove(request.id)}
                        >
                          {t("Approve")}
                        </button>
                      )}
                      <button
                        className="btn btn-danger"
                        onClick={() => handleReject(request.id)}
                      >
                        {t("Reject")}
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      </div>
    </div>
  );
};

export default VerificationRequestsPage;
