import React from "react";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { logoutSucces } from "../redux/authActions";
import ProfileImageWithDefault from "../components/ProfileImageWithDefault";

const Sidebar = () => {
  const { displayName, isLoggedIn, image, username } = useSelector((store) => ({
    isLoggedIn: store.isLoggedIn,
    displayName: store.displayName,
    image: store.image,
    username: store.username,
  }));

  const dispatch = useDispatch();

  const onLogoutSuccess = () => {
    dispatch(logoutSucces());
  };

  const sidebarStyle = {
    position: "sticky",
    top: "50%",
    transform: "translateY(-50%)",
  };

  return (
    <div className="col-lg-2 d-none d-lg-block" style={sidebarStyle}>
      <div className="card">
        <div className="card-body">
          <ul className="list-group">
            {isLoggedIn && (
              <>
                <li className="list-group-item list-group-item-action">
                  <Link
                    to={"/users/" + username}
                    className="d-flex align-items-center text-decoration-none"
                  >
                    <ProfileImageWithDefault
                      image={image}
                      width="24"
                      height="24"
                      className="rounded-circle me-1"
                    />
                    <span>{displayName}</span>
                  </Link>
                </li>
                <li className="list-group-item list-group-item-action">
                  <Link
                    to={"/users/" + username}
                    className="d-flex align-items-center text-decoration-none"
                  >
                    <i className="material-icons text-info me-1">person</i>
                    My Profile
                  </Link>
                </li>
              </>
            )}
            <li className="list-group-item list-group-item-action">
              <i className="fas fa-user me-2"></i> Profile
            </li>
            <li className="list-group-item list-group-item-action">
              <i className="fas fa-envelope me-2"></i> Messages
            </li>
            <li className="list-group-item list-group-item-action">
              <i className="fas fa-bell me-2"></i> Notifications
            </li>
            <li className="list-group-item list-group-item-action">
              <i className="fas fa-circle me-2"></i> Friends
            </li>
            <li className="list-group-item list-group-item-action">
              <i className="fas fa-circle-info me-2"></i> Verify
            </li>
            <li className="list-group-item list-group-item-action">
              <i className="fas fa-user me-2"></i> Help
            </li>
            <li className="list-group-item list-group-item-action">
              <i className="fas fa-cog me-2"></i> Settings
            </li>

            {isLoggedIn && (
              <li
                className="list-group-item list-group-item-action text-decoration-none"
                onClick={onLogoutSuccess}
                style={{ cursor: "pointer" }}
              >
                <Link
                  to="/login"
                  className="d-flex align-items-center text-decoration-none"
                >
                  <i className="material-icons text-warning me-1">logout</i>
                  Logout
                </Link>
              </li>
            )}
            {/* Add more buttons for other functionalities */}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
