import React from "react";
import LanguageSelector from "../components/LanguageSelector";
import logo from "../assets/logo.png";
import { Link } from "react-router-dom";
import { useTranslation } from "react-i18next";
import { useDispatch, useSelector } from "react-redux";
import { logoutSucces } from "../redux/authActions";
import ProfileImageWithDefault from "../components/ProfileImageWithDefault";

const TopBar = (props) => {
  const { t } = useTranslation();

  const { username, isLoggedIn, displayName, image } = useSelector((store) => ({
    isLoggedIn: store.isLoggedIn,
    username: store.username,
    displayName: store.displayName,
    image: store.image,
  }));

  const dispatch = useDispatch();

  const onLogoutSuccess = () => {
    dispatch(logoutSucces());
  };

  let links = (
    <div className="ms-auto">
      <ul className="navbar-nav">
        <li className="nav-item">
          <Link className="nav-link" to="/">
            {t("Home Page")}
          </Link>
        </li>
        <li className="nav-item">
          <Link className="nav-link" to="/login">
            {t("Login")}
          </Link>
        </li>
        <li className="nav-item">
          <Link className="nav-link" to="/signup">
            {t("Sign Up")}
          </Link>
        </li>
        <li className="nav-item">
          <LanguageSelector />
        </li>
      </ul>
    </div>
  );

  if (isLoggedIn) {
    links = (
      <div className="ms-auto">
        <ul className="navbar-nav">
          <li className="nav-item">
            <Link className="nav-link d-flex" to="/">
              <i className="material-icons text-secondary me-1">home</i>
              {t("Home Page")}
            </Link>
          </li>
          <li className="nav-item dropdown">
            <a
              href="#"
              id="navbarDropdown"
              role="button"
              data-bs-toggle="dropdown"
              aria-expanded="false"
              className="nav-link d-flex"
            >
              <ProfileImageWithDefault
                image={image}
                width="32"
                height="32"
                className="rounded-circle me-1"
              />
              <span>{displayName}</span>
            </a>
            <ul className="dropdown-menu" aria-labelledby="navbarDropdown">
              <li>
                <Link className="dropdown-item d-flex" to={"/users/" + username}>
                  <i className="material-icons text-info me-1">person</i>
                  {t("My Profile")}
                </Link>
              </li>
              <li onClick={onLogoutSuccess}>
                <Link className="dropdown-item d-flex" to="/login">
                  <i className="material-icons text-warning me-1">logout</i>
                  {t("Logout")}
                </Link>
              </li>
            </ul>
          </li>

          <li className="nav-item">
            <LanguageSelector />
          </li>
        </ul>
      </div>
    );
  }

  return (
    <div className="shadow-sm bg-light">
      <nav
        className={`navbar navbar-light container navbar-expand-lg ${
          isLoggedIn ? "justify-content-between" : ""
        }`}
      >
        {!isLoggedIn && (
          <>
            <Link className={`navbar-brand ${isLoggedIn ? "d-none" : ""}`} to="/">
              <img src={logo} width="65" alt="Hoaxify logo" />
              Social Cafe
            </Link>

            {links}
          </>
        )}
        {isLoggedIn && (
          <>
            <button
              class={`navbar-toggler ${isLoggedIn ? "" : "d-none"}`}
              type="button"
              data-bs-toggle="collapse"
              data-bs-target="#navbarNavDropdown"
              aria-controls="navbarNavDropdown"
              aria-expanded="false"
              aria-label={t("Toggle navigation")}
            >
              <span class={`navbar-toggler-icon ${isLoggedIn ? "" : "d-none"}`}></span>
            </button>

            <div class={`collapse navbar-collapse`} id={`navbarNavDropdown`}>
              {links}
            </div>

            {/* Logo */}
            {/* Only visible on mobile */}
            {/* Hidden on desktop */}
            {/* Visible on mobile */}
            {/* Hidden on desktop */}
            {/* Visible on mobile */}
            {/* Hidden on desktop */}
            {/* Visible on mobile */}
            {/* Hidden on desktop */}
            {/* Visible on mobile */}
            {/* Hidden on desktop */}
            {/* Visible on mobile */}
            {/* Hidden on desktop */}
            {/* Visible on mobile */}
            {/* Hidden on desktop */}
            {/* Visible on mobile */}
            {/* Hidden on desktop */}

            {/* Logo */}
            {/* Only visible on desktop */}
            {/* Hidden on mobile */}

            {/* Logo */}
          </>
        )}
      </nav>
    </div>
  );
};

export default TopBar;
