import React, { useState } from 'react';

const VerifiedBadge = () => {
  const [modalVisible, setModalVisible] = useState(false);

  const openModal = () => {
    setModalVisible(true);
  };

  const closeModal = () => {
    setModalVisible(false);
  };

  return (
    <>
      <span
        className="d-inline-block"
        tabIndex="0"
        data-toggle="modal"
        data-target="#verificationModal"
        data-placement="top"
        title="Onaylanmış Kullanıcı"
        style={{ cursor: 'pointer' }}
        onClick={openModal}
      >
        <i className="material-icons text-info align-middle ms-2">
          verified
        </i>
      </span>

      {/* Bootstrap Modal */}
      <Modal show={modalVisible} onHide={closeModal} centered>
        <Modal.Header closeButton>
          <Modal.Title>Onaylanmış Kullanıcı</Modal.Title>
        </Modal.Header>
        <Modal.Body>Kullanıcı hesabı onaylandı.</Modal.Body>
        <Modal.Footer>
          <button className="btn btn-secondary" onClick={closeModal}>
            Kapat
          </button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default VerifiedBadge;
