import React, { Component } from "react";
import { getUsers } from "../api/apiCalls";
import { useTranslation, withTranslation } from "react-i18next";
import "bootstrap/dist/css/bootstrap.min.css";
import UserListItem from "./UserListItem";
class UserList extends Component {
  state = {
    page:{
      content:[],
      size:10,
      number:0
    }
  };
  componentDidMount() {
    this.loadUser();
  }

  onClickPrevious = () =>{
    const previousPage = this.state.page.number - 1;
    this.loadUser(previousPage);
  }
  onClickNext = () =>{
    const nextPage = this.state.page.number + 1;
    this.loadUser(nextPage);
  }

  loadUser = page => {
    getUsers(page).then(response=>{
      this.setState({
        page:response.data
      });
    });
  }

  render() {
    const { t } = useTranslation();
    const { content:users, last,first } = this.state.page;
    return (
      <div className="card">
        <h3 className="card-header text-center">{t("Online Users")}</h3>
        <div className="list-group list-group-flush">
          {users.map((user) => (
            <UserListItem key={user.username} user={user}/>
          ))}
        </div>
        <div className="d-flex justify-content-between" style={{
          margin:"4px"
        }}>
          {first === false && (
            <button className="btn btn-primary" onClick={this.onClickPrevious}>
              {t("Previous")}
            </button>
          )}
          {last === false && (
            <button className="btn btn-secondary" onClick={this.onClickNext}>
              {t("Next")}
            </button>
          )}
        </div>
      </div>
    );
  }
}

export default UserList;
