import React, {useEffect} from "react";
import LoginPage from "../pages/LoginPage";
import UserSignupPage from "../pages/UserSignupPage";
import HomePage from "../pages/HomePage";
import UserPage from "../pages/UserPage";
import Footer from "./Footer";
import {HashRouter as Router, Route, Redirect,Switch} from "react-router-dom";
import TopBar from "./TopBar";
import { useSelector, useDispatch } from "react-redux";
import VerificationRequestsPage from "../pages/VerificationRequestsPage";
import { loadLikedPostsFromStorage } from "./redux/authActions";

const App = () => {


  const dispatch = useDispatch();

  useEffect(() => {
    const likedPosts = loadLikedPostsFromStorage();
    dispatch({ type: 'LIKE_POSTS_FROM_STORAGE', payload: likedPosts });
  }, [dispatch]);

  const { isLoggedIn,admin } = useSelector((store) => ({
    isLoggedIn: store.isLoggedIn,
    admin: store.admin,
  }));
  return (
    <div>
      <Router>
        {!false &&  <TopBar />}
        
        <Switch>
          <Route exact path="/" component={HomePage} />
          {!isLoggedIn && <Route path="/login" component={LoginPage} />}
          <Route path="/signup" component={UserSignupPage} />
          <Route path="/users/:username" component={UserPage} />
          {isLoggedIn && admin &&(
            <Route path="/verifications/applications" component={VerificationRequestsPage} />
          )}
          <Redirect to="/" />
        </Switch>
        <div>
          <Footer />
        </div>
      </Router>
    </div>
  );
};

export default App;
