import React, { useEffect } from "react"; // useEffect'i import etmeyi unutmayın
import i18n from "i18next";
import { initReactI18next } from "react-i18next";
import { fetchTranslationFromAPI } from "../src/api/apiCalls";
import { register } from "timeago.js";

i18n.use(initReactI18next).init({
  resources: {},
  fallbackLng: "en",
  ns: ["translations"],
  defaultNS: "translations",
  keySeperator: false,
  interpolation: {
    escapeValue: false,
    formatSeperator: ",",
  },
  react: {
    wait: true,
  },
});

const timeagoTR = (number, index) => {
  // ... zaman ifadeleri
};
register("tr", timeagoTR);

// İşlev bileşeni içinde useEffect kullanımı
function I18nProvider() {
  useEffect(() => {
    loadTranslations("en");
  }, []);

  async function loadTranslations(language) {
    const translations = await fetchTranslationFromAPI(language);
    i18n.addResourceBundle(language, "translations", translations, true, true);
  }

  // Diğer bileşen kodları
}

export default I18nProvider;
