import React, { useEffect, useState } from "react";
import { getUsers } from "../api/apiCalls";
import { useTranslation } from "react-i18next";
import "bootstrap/dist/css/bootstrap.min.css";
import UserListItem from "./UserListItem";
const UserList = () => {
  const [page, setPage] = useState({
    content: [],
    size: 10,
    number: 0,
  });


  useEffect(() => {
    loadUser();
  }, []);

  const onClickPrevious = () => {
    const previousPage = page.number - 1;
    loadUser(previousPage);
  };
  const onClickNext = () => {
    const nextPage = page.number + 1;
    loadUser(nextPage);
  };

  const loadUser = (page) => {
    getUsers(page).then((response) => {
      setPage(response.data);
    });
  };

  const { t } = useTranslation();
  const pendingApiCall = useApiProgress("/api/1.0/users?page");
  const { content: users, last, first } = page;
  let actionDiv = (
    <div
      className="d-flex justify-content-between"
      style={{
        margin: "4px",
      }}
    >
      {first === false && (
        <button className="btn btn-primary" onClick={onClickPrevious}>
          {t("Previous")}
        </button>
      )}
      {last === false && (
        <button className="btn btn-secondary float-end" onClick={onClickNext}>
          {t("Next")}
        </button>
      )}
    </div>
  );
  if (pendingApiCall) {
    actionDiv = (
      <div className="d-flex justify-content-center">
        <div className="spinner-border text-black-50">
          <span className="sr-only">Loading...</span>
        </div>
      </div>
    );
  }
  return (
    <div className="card">
      <h3 className="card-header text-center">{t("Online Users")}</h3>
      <div className="list-group list-group-flush">
        {users.map((user) => (
          <UserListItem key={user.username} user={user} />
        ))}
      </div>
      {actionDiv}
    </div>
  );
};

export default UserList;
