import * as ACTIONS from "./Constants";

const defaultState = {
  isLoggedIn: false,
  username: undefined,
  displayName: undefined,
  image: undefined,
  password: undefined,
  admin: undefined,
};

const authReducer = (state = { ...defaultState }, action) => {
  console.log("Action Type:", action.type); // Action tipini logla
  console.log("Action Payload:", action.payload); 
  if (action.type === ACTIONS.LOGOUT_SUCCES) {
    return defaultState;
  } else if (action.type === ACTIONS.LOGIN_SUCCES) {
    return {
      ...action.payload,
      isLoggedIn: true,
    };
  } else if (action.type === ACTIONS.UPDATE_SUCCESS) {
    return { ...state, ...action.payload };
  }else if (action.type === ACTIONS.UPDATE_ADMIN_STATUS) {
    return { ...state, admin: action.payload.isAdmin };
  }
  return state;
};

export default authReducer;
