import React, { useState } from 'react';

const VerifiedBadge = () => {
  const [modalVisible, setModalVisible] = useState(false);

  const openModal = () => {
    setModalVisible(true);
  };

  const closeModal = () => {
    setModalVisible(false);
  };

  return (
    <>
      <span
        className="d-inline-block"
        tabIndex="0"
        onClick={openModal}
        data-toggle="tooltip"
                  data-placement="top"
                  title="Onaylanmış Kullanıcı"
        style={{ cursor: 'pointer' }}
      >
        <i className="material-icons text-info align-middle ms-2">
          verified
        </i>
      </span>

      {modalVisible && (
        <div className="modal fade show" tabIndex="-1" style={{ display: 'block' }}>
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Modal Başlığı</h5>
                <button type="button" className="btn-close" onClick={closeModal}></button>
              </div>
              <div className="modal-body">
                Modal içeriği buraya gelecek.
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-secondary" onClick={closeModal}>Kapat</button>
                <button type="button" className="btn btn-primary">Kaydet</button>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default VerifiedBadge;
