import React, { useEffect, useState } from "react";
import maintenance from "../assets/maintenance2.jpg";
import ProfileCard from "../components/ProfileCard";
import { getUser } from "../api/apiCalls";
import { useParams } from "react-router-dom/cjs/react-router-dom.min";
import { useTranslation } from "react-i18next";
import { useApiProgress } from "../shared/ApiProgress";
import Spinner from "../components/Spinner";
import PostFeed from "../components/PostFeed";
import Sidebar from "../components/Sidebar";
import LikedPostFeed from "../components/LikedPostFeed";
const UserPage = () => {
  const [user, setUser] = useState({});
  const [notFound, setNotFound] = useState(false);
  const { username } = useParams();
  const { t } = useTranslation();
  const [activeTab, setActiveTab] = useState("POSTS");
  const pendingApiCall = useApiProgress(
    "get",
    "/api/1.0/users/" + username,
    true
  );
  const [showLikedPosts, setShowLikedPosts] = useState(false);
  useEffect(() => {
    setNotFound(false);
  }, [user]);

  useEffect(() => {
    const loadUser = async () => {
      try {
        const response = await getUser(username);
        setUser(response.data);
      } catch (error) {
        setNotFound(true);
      }
    };
    loadUser();
  }, [username]);
  if (notFound) {
    return (
      <div className="container">
        {" "}
        <div className="alert alert-danger text-center">
          <div>
            <i className="material-icons" style={{ fontSize: "48px" }}>
              error
            </i>
          </div>
          {t("User not found!")}
        </div>
      </div>
    );
  }
  if (pendingApiCall || user.username !== username) {
    return <Spinner />;
  }

  const loadUserPosts = () => {
    setActiveTab("POSTS");
    setShowLikedPosts(false);
    console.log("POSTS"); // "LİKES" seçeneğine tıklanınca aktif sekmeyi güncelle
     // "POSTS" seçeneğine tıklanınca aktif sekmeyi güncelle
  };

  const loadUserLikes = () => {
    setActiveTab("LIKES");
    setShowLikedPosts(true);
    console.log("Likes"); // "LİKES" seçeneğine tıklanınca aktif sekmeyi güncelle
  };
  return (
    <div class="container mt-3">
      <div class="row">
        <div className="col-md-2">
          <Sidebar user={user} />
        </div>
        <div className="col-md-6">
          <div className="d-flex justify-content-between mb-2">
            <div
              className={`card text-center text-primary item-action me-2 px-5 ${
                activeTab === "POSTS" ? "active-tab" : ""
              }`}
              style={{cursor:"pointer"}}
              onClick={loadUserPosts}
            >
              {t("POSTS")}
            </div>
            <div
              className={`card text-center text-primary ms-2 px-5 ${
                activeTab === "LIKES" ? "active-tab" : ""
              }`}
              style={{cursor:"pointer"}}
              onClick={loadUserLikes}
            >
              {t("LİKES")}
            </div>
          </div>
          {activeTab === "POSTS" && !showLikedPosts && <PostFeed username={username} />}
  {/* Beğenilen postları göster */}
  {activeTab === "LIKES" && (showLikedPosts || !activeTab) && <LikedPostFeed username={username} />}
          <PostFeed />
        </div>
        <div className="col-md-4">
          <ProfileCard user={user} />
          <h1 class="text-center">{t("Profile")}</h1>
          <img
            class="rounded mx-auto d-block"
            width="200"
            src={maintenance}
            alt="Maintenance"
          />
        </div>
      </div>
    </div>
  );
};

export default UserPage;
