import React, { useEffect, useState } from "react";
import { getLikedPosts } from "../api/apiCalls";
import PostView from "./PostView";
import Spinner from "../components/Spinner";
import { useParams } from "react-router-dom";
import { useTranslation } from "react-i18next";
const LikedPostFeed = ({ username }) => {
  const [postPage, setPostPage] = useState({
    content: [],
    last: true,
    number: 0,
  });
  const { t } = useTranslation();
  const [newPostCount, setNewPostCount] = useState(0);

  const onDeletePostSuccess = (id) => {
    setPostPage((previousPostPage) => ({
      ...previousPostPage,
      content: previousPostPage.content.filter((post) => post.id !== id),
    }));
  };
  useEffect(() => {
    const loadLikedPosts = async (page) => {
      try {
        const response = await getLikedPosts(username,page);
        console.log("API Response:", response.data); // API yanıtını kontrol et
        setPostPage((previousPostPage) => ({
          ...response.data,
          content: [...previousPostPage.content, ...response.data.content],
        }));
      } catch (error) {
        console.error("Error loading liked posts:", error);
      }
    };
    loadLikedPosts();
  }, [username]);

  const { content, last } = postPage;

  return (
    <div>
      {content.map((post) => (
        <div key={post.id} className="mb-">
          <PostView post={post} onDeletePost={onDeletePostSuccess}></PostView>
        </div>
      ))}
      {!last && (
        <div
          className="alert alert-secondary text-center"
          style={{ cursor: loadOldPostProgress ? "wait" : "pointer" }}
        >
        </div>
      )}
    </div>
  );
};

export default LikedPostFeed;
