import React, { useEffect, useState } from "react";
import { getPosts } from "../api/apiCalls";
import { useTranslation } from "react-i18next";
import PostView from "./PostView";

const PostFeed = () => {
  const [postPage, setPostPage] = useState({ content: [], last: true });
  const { t } = useTranslation();
  useEffect(() => {
    const loadPosts = async () => {
      try {
        const response = await getPosts();
        setPostPage(response.data);
      } catch (error) {}
    };
    loadPosts();
  }, []);

  const { content, last } = postPage;

  if (postPage.content.length === 0) {
    return (
      <div className="alert alert-secondary text-center">
        {t("There are no posts")}
      </div>
    );
  }

  return (
    <div>
      {content.map((post) => {
        return <PostView key={post.id} post={post}></PostView>;
      })}
      {!last && (
        <div className="alert alert-secondary text-center">
          {t("There are no posts")}
        </div>
      )}
    </div>
  );
};

export default PostFeed;
