import React from "react";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { logoutSucces } from "../redux/authActions";
import ProfileImageWithDefault from "./ProfileImageWithDefault";
import { useTranslation } from "react-i18next";
import logo from "../assets/logo.png";

const Sidebar = () => {
  const { displayName, isLoggedIn, image, username } = useSelector((store) => ({
    isLoggedIn: store.isLoggedIn,
    displayName: store.displayName,
    image: store.image,
    username: store.username,
  }));
  const { t } = useTranslation();
  const dispatch = useDispatch();

  const onLogoutSuccess = () => {
    dispatch(logoutSucces());
  };

  return (
    <div
      className="position-sticky top-0 start-0 bottom-1 bg-white text-dark ps-5 mb-0 pb-0"
      style={{ width: "250px", left: "50%", transform: "translateX(-50%)" }}
    >
      {isLoggedIn && (
        <div className="card">
          <div className="card-body">
            <ul className="list-group">
              <Link className="list-group-item text-center" to="/">
                <img src={logo} width="48" alt="Hoaxify logo" />
              </Link>
              <li className="list-group-item list-group-item-action">
                <Link
                  to="/"
                  className="d-flex align-items-center text-secondary"
                  style={{ textDecoration: "none" }}
                >
                  <i className="fas fa-home me-2"></i>
                  {t("Home")}
                </Link>
              </li>
              <li className="list-group-item list-group-item-action">
                <Link
                  to={"/users/" + username}
                  className=" text-dark"
                  style={{ textDecoration: "none" }}
                >
                  <ProfileImageWithDefault
                    image={image}
                    width="24"
                    height="24"
                    className="rounded-circle me-2"
                  />
                  <span>{displayName}</span>
                </Link>
              </li>
              <li className="list-group-item list-group-item-action">
                <Link
                  to={"/users/" + username}
                  className="d-flex align-items-center text-primary"
                  style={{ textDecoration: "none" }}
                >
                  <i className="fas fa-user me-2"></i>
                  {t("My Profile")}
                </Link>
              </li>

              {/* Other sidebar items go here */}

              <li className="list-group-item list-group-item-action">
                <Link
                  to="/login"
                  className="d-flex align-items-center"
                  style={{ textDecoration: "none" }}
                  onClick={onLogoutSuccess}
                >
                  <i className="fas fa-right-from-bracket me-2"></i>
                  {t("Logout")}
                </Link>
              </li>
            </ul>
          </div>
        </div>
      )}
    </div>
  );
};

export default Sidebar;
