import React, { useState, useEffect } from "react";

const Footer = () => {
  const currentYear = new Date().getFullYear();
  const [isVisible, setIsVisible] = useState(true);

  useEffect(() => {
    // Set the time duration (in milliseconds) after which the footer will be hidden
    const hideAfterMs = 5000; // 5 seconds in this example

    // Timer to hide the footer after the specified duration
    const timer = setTimeout(() => {
      setIsVisible(false);
    }, hideAfterMs);

    // Clear the timer when the component is unmounted to avoid any memory leaks
    return () => clearTimeout(timer);
  }, []);

  // If isVisible is false, the footer will not be rendered
  return (
    isVisible && (
