import * as ACTIONS from './Constants'


const defaultState = {
    isLoggedIn: false,
    username: undefined,
    displayName: undefined,
    image: undefined,
    password: undefined,
  };
  
  const authReducer = (state= {...defaultState}, action) => {
    if (action.type === "logout-success") {
      return defaultState;
    }
    else if(action.type ==='login-success'){
      return{
        ...action.payload,
        isLoggedIn: true
      }
      
    }
    return state;
  };

  export default authReducer;
