import i18n from "i18next";
import { initReactI18next } from "react-i18next";
import { fetchTranslationFromAPI } from "../src/api/apiCalls";
import { register } from "timeago.js";

i18n.use(initReactI18next).init({
  resources: {},
  fallbackLng: "en",
  ns: ["translations"],
  defaultNS: "translations",
  keySeperator: false,
  interpolation: {
    escapeValue: false,
    formatSeperator: ",",
  },
  react: {
    wait: true,
  },
});
async function loadTranslations(language) {
  try {
    const response = await fetchTranslationFromAPI("translations", language);
    i18n.addResourceBundle(language, "translations", response, true, true);
  } catch (error) {
    console.error("Error loading translations:", error);
  }
}


i18n.on("languageChanged", (lng) => {
  loadTranslations(lng);
});

// Timeago dil desteği ekleme
const timeagoTR = (number, index) => {
  // ... zaman ifadeleri
};
register("tr", timeagoTR);

// Varsayılan olarak İngilizce metinleri yükle
loadTranslations("en");

export default i18n;
