import React, { useState } from "react";
import { signup } from "../api/apiCalls";
import Input from "../components/Input";
import { useTranslation } from "react-i18next";
import ButtonWithProgress from "./ButtonWithProgress";
import { useApiProgress } from "../shared/ApiProgress";
import background from "../assets/1.jpg";
import { useDispatch } from "react-redux";
import { signupHandler } from "../redux/authActions";
import "bootstrap/dist/css/bootstrap.min.css";

const UserSignupPage = (props) => {
  const [form, setForm] = useState({
    username: null,
    displayName: null,
    password: null,
    passwordRepeat: null,
  });

  const [errors, setErrors] = useState({});
  const dispatch = useDispatch();
  const onChange = (event) => {
    const { name, value } = event.target;
    const errorsCopy = { ...errors };
    errorsCopy[name] = undefined;
    setErrors(errorsCopy);
    const formCopy = { ...form };
    formCopy[name] = value;
    setForm((previousForm) => ({ ...previousForm, [name]: value }));
  };

  const onClickSignup = async (event) => {
    event.preventDefault();
    const { history } = props;
    const { push } = history;
    const { username, displayName, password } = form;
    const body = {
      username,
      displayName,
      password,
    };

    try {
      const response = await dispatch(signupHandler(body));
      push("/");
    } catch (error) {
      if (error.response.data.validationErrors) {
        setErrors(error.response.data.validationErrors);
      }
    }
  };

  const { t } = useTranslation();

  const {
    username: usernameError,
    displayName: displayNameError,
    password: passwordError,
  } = errors;
  const pendingApiCallSignup = useApiProgress("/api/1.0/users");
  const pendingApiCallLogin = useApiProgress("/api/1.0/auth");
  const pendingApiCall = pendingApiCallSignup || pendingApiCallLogin;
  let passwordRepeatError;
  if (form.password !== form.passwordRepeat) {
    passwordRepeatError = t("Password mismatch");
  }

  return (
    <div
      className="container-fluid d-flex justify-content-center align-items-center vh-100"
      style={{
        backgroundImage: `url(${background})`,
        backgroundSize: "cover",
        backgroundColor: "rgba(192, 192, 192, 1)",
      }}
    >
      <div
        className="container p-4 col-10 col-md-8 col-lg-6"
        style={{
          backgroundColor: "rgba(255, 255, 255, 0.8)",
          borderRadius: "20px",
        }}
      >
        <form>
          <h1 className="text-center mb-3">{t("Sign Up")}</h1>
          <div className="row">
            <div className="col-12 col-md-6">
              <Input
                name="username"
                label={t("Username")}
                error={usernameError}
                onChange={onChange}
              />
            </div>
            <div className="col-12 col-md-6">
              <Input
                name="displayName"
                label={t("Display Name")}
                error={displayNameError}
                onChange={onChange}
              />
            </div>
          </div>
          <div className="row">
            <div className="col-12 col-md-6">
              <Input
                name="password"
                label={t("Password")}
                type="password"
                error={passwordError}
                onChange={onChange}
              />
            </div>
            <div className="col-12 col-md-6">
              <Input
                name="passwordRepeat"
                label={t("Password Repeat")}
                type="password"
                error={passwordRepeatError}
                onChange={onChange}
              />
            </div>
          </div>
          <div className="text-center mt-3">
            <ButtonWithProgress
              onClick={onClickSignup}
              disabled={pendingApiCall || passwordRepeatError !== undefined}
              pendingApiCall={pendingApiCall}
              text={t("Sign Up")}
            />
          </div>
        </form>
      </div>
    </div>
  );
};

export default UserSignupPage;
