import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { useSelector } from "react-redux";
import { getOldPosts } from "../api/apiCalls";
import ProfileImageWithDefault from "../components/ProfileImageWithDefault";

const PostViewDetails = () => {
  const loggedInUser = useSelector((store) => store.username);
  const [post, setPost] = useState({});
  const [comments, setComments] = useState([]);
  const { postId } = useParams();

  useEffect(() => {
    const loadPost = async () => {
      try {
        const response = await getOldPosts(postId,loggedInUser);
        setPost(response.data);
      } catch (error) {
        console.error("Error while loading post:", error);
      }
    };
    loadPost();
  }, [postId]);

  useEffect(() => {
    const loadComments = async () => {
      try {
        const response = await getOldPosts(postId,loggedInUser);
        setComments(response.data);
      } catch (error) {
        console.error("Error while loading comments:", error);
      }
    };
    loadComments();
  }, [postId]);

  return (
    <div className="card p-3">
      <div className="d-flex">
        <ProfileImageWithDefault
          Link={`/users/${post.user?.username}`}
          image={post.user?.image}
          width="32"
          height="32"
          className="rounded-circle m-1"
        />
        <div className="flex-fill m-auto px-2">
          <h6 className="d-inline">
            <span>{post.user?.displayName}</span>
            <span className="text-muted"> @{post.user?.username}</span>
          </h6>
          {/* Diğer post bilgileri */}
        </div>
      </div>
      <div className="post-content my-3">{post.content}</div>
      {/* Eğer dosya eki varsa göster */}
      {/* Yorumları listele */}
      {comments.map((comment) => (
        <div key={comment.id} className="card my-3 p-2">
          {/* Yorum bilgileri */}
        </div>
      ))}
      {/* Yorum yapma alanı */}
    </div>
  );
};

export default PostViewDetails;
