import React, { useState } from "react";
import { useTranslation } from "react-i18next";
import { createVerificationRequest } from "../api/apiCalls";

const VerificationRequestForm = () => {
  const [user, setUser] = useState(null); // User bilgisini alacağınız state
  const [reason, setReason] = useState("");
  const [attachment, setAttachment] = useState(null); // Dosya eklerken kullanacağınız state
  const [verifiedRequest, setVerifiedRequest] = useState(false);
  const [pendingApiCall, setPendingApiCall] = useState(false);
  const { t } = useTranslation();

  const handleCreateRequest = async () => {
    setPendingApiCall(true);
    try {
      const requestData = {
        user: user.id, // Burada user nesnesinin ID'sini kullanın
        reason: reason,
        attachment: attachment,
      };

      await createVerificationRequest(requestData);
      setPendingApiCall(false);
      setVerifiedRequest(false);
      console.log("Verification request created successfully.");
    } catch (error) {
      console.error("Error creating verification request:", error);
      setPendingApiCall(false);
    }
  };

  return (
    <div>
      {true && (
        <div>
          {/* Kullanıcı girişi veya seçimi */}
          {/* Reason girişi */}
          {/* Dosya yükleme alanı */}
          <button
            className="btn btn-primary d-inline-flex mr-2"
            onClick={handleCreateRequest}
            disabled={pendingApiCall}
          >
            <i className="material-icons">save</i>
            {t("Save")}
          </button>
          <button
            className="btn btn-light d-inline-flex ml-1"
            onClick={() => setVerifiedRequest(false)}
            disabled={pendingApiCall}
          >
            <i className="material-icons">close</i>
            {t("Cancel")}
          </button>
        </div>
      )}
    </div>
  );
};

export default VerificationRequestForm;
