import React, { useEffect } from "react";
import { useTranslation } from "react-i18next";
import { changeLanguage } from "../api/apiCalls";
import { fetchTranslationFromAPI } from "..c/api/apiCalls";


const LanguageSelector = () => {
  const { i18n } = useTranslation();

  const onChangeLanguage = (language) => {
    i18n.changeLanguage(language);
    // Sunucuya dil değişimini iletmeye gerek kalmadığı için changeLanguage çağrısını kaldırdım
  };

  useEffect(() => {
    loadTranslations(i18n.language); // Bileşen ilk render olduğunda ve dil değiştiğinde çalışır
  }, [i18n.language]); // Dil değiştiğinde tekrar çalıştır

  async function loadTranslations(language) {
    const translations = await fetchTranslationFromAPI(language);
    i18n.addResourceBundle(
      language,
      "translations",
      translations,
      true,
      true
    );
  }

  return (
    <div className="">
      <div className="d-flex flex-row-reverse">
        <img
          src="https://flagsapi.com/US/flat/32.png"
          alt="USA Flag"
          onClick={() => onChangeLanguage("en")}
          style={{ cursor: "pointer" }}
        />
        <img
          src="https://flagsapi.com/TR/flat/32.png"
          alt="Turkish Flag"
          onClick={() => onChangeLanguage("tr")}
          style={{ cursor: "pointer" }}
        />
      </div>
    </div>
  );
};

export default LanguageSelector;
