import React, { useEffect, useState } from "react";
import { useTranslation } from "react-i18next";
import ProfileImageWithDefault from "./ProfileImageWithDefault";
import { useSelector } from "react-redux";
import { postIt, postPostAttachment } from "../api/apiCalls";
import { useApiProgress } from "../shared/ApiProgress";
import ButtonWithProgress from "../pages/ButtonWithProgress";
import Input from "../components/Input";

const PostSubmit = () => {
  const { t } = useTranslation();
  const { image } = useSelector((store) => ({ image: store.image }));
  const [focused, setFocused] = useState(false);
  const [post, setPost] = useState("");
  const [errors, setErrors] = useState({});
  const [newImage, setNewImage] = useState();

  useEffect(() => {
    if (!focused) {
      setPost("");
      setErrors({});
      setNewImage();
    }
  }, [focused]);

  useEffect(() => {
    setErrors({});
  }, [post]);

  const pendingApiCall = useApiProgress("post", "/api/1.0/posts");

  const onClickPostIt = async () => {
    const body = {
      content: post,
    };
    try {
      await postIt(body);
      setFocused(false);
    } catch (error) {
      if (error.response.data.validationErrors) {
        setErrors(error.response.data.validationErrors);
      }
    }
  };

  const onChangeFile = (event) => {
    if (event.target.files.length < 1) {
      return;
    }

    const file = event.target.files[0];
    const fileReader = new FileReader();
    fileReader.onloadend = () => {
      setNewImage(fileReader.result);
    };
    fileReader.readAsDataURL(file);
    uploadFile(file);
  };

  const uploadFile = async (file) =>{
    const attachment = new FormData();
    attachment.append('file',file);
    await postPostAttachment();
  }


  let textAreaClass = "mb-2 form-control";
  if (errors.content) {
    textAreaClass += " is-invalid";
  }

  return (
    <div className="card p-2">
      <div className="d-flex flex-wrap align-items-start">
        <div className="col-2 col-md-1">
          <ProfileImageWithDefault
            className="rounded-circle"
            image={image}
            width="32"
            height="32"
          />
        </div>
        <div className="col-10 col-md-11">
          <textarea
            className={textAreaClass}
            rows={focused ? "4" : "3"}
            onFocus={() => setFocused(true)}
            placeholder={t("What's happening?")}
            onChange={(event) => {
              setPost(event.target.value);
            }}
            value={post}
          ></textarea>

          <div className="invalid-feedback">{errors.content}</div>

          
            {focused && (
              <>
                <Input type="file" onChange={onChangeFile} />
                {newImage && <img className="img- img-thumbnail" src={newImage} alt="post-attachment" />}
                <div className="d-flex justify-content-end text-right d-flex align-items-center">
                  <ButtonWithProgress
                    className="btn btn-info m-1"
                    onClick={onClickPostIt}
                    pendingApiCall={pendingApiCall}
                    disabled={pendingApiCall}
                    text={t(" TalkIt")}
                    icon="fa-regular fa-paper-plane fa-beat me-2 mr-2"
                  />
                  {/* <i className="material-icons me-1">send</i> */}
                  {/* <span className="ml-1">{t(" TalkIt")}</span> */}
                  <button
                    className="btn btn-light"
                    onClick={() => setFocused(false)}
                    disabled={pendingApiCall}
                  >
                    <i class="fa-solid fa-ban me-2"></i>
                    {t("Cancel")}
                  </button>
                </div>
              </>
            )}
          
        </div>
      </div>
    </div>
  );
};

export default PostSubmit;
