import React, { useEffect, useState } from "react";
import { useTranslation } from "react-i18next";
import { getVerificationRequests, approveVerificationRequest, rejectVerificationRequest } from "../api/apiCalls";

const VerificationRequestsPage = () => {
  const [verificationRequests, setVerificationRequests] = useState([]);
  const { t } = useTranslation();

  useEffect(() => {
    loadVerificationRequests();
  }, []);

  const loadVerificationRequests = async () => {
    try {
      const response = await getVerificationRequests();
      setVerificationRequests(response.data);
    } catch (error) {
      // Handle error
    }
  };

  const handleApprove = async (requestId) => {
    try {
      await approveVerificationRequest(requestId);
      loadVerificationRequests();
    } catch (error) {
      // Handle error
    }
  };

  const handleReject = async (requestId) => {
    try {
      await rejectVerificationRequest(requestId);
      loadVerificationRequests();
    } catch (error) {
      // Handle error
    }
  };

  return (
    <div className="container">
      <h2>{t("Unverified User Applications")}</h2>
      <table className="table table-striped">
        <thead>
          <tr>
            <th>{t("Username")}</th>
            <th>{t("Display Name")}</th>
            <th>{t("Reason")}</th>
            <th>{t("Attachment")}</th>
            <th>{t("Actions")}</th>
          </tr>
        </thead>
        <tbody>
          {verificationRequests.map((request) => (
            <tr key={request.id}>
              <td>{request.user.username}</td>
              <td>{request.user.displayName}</td>
              <td>{request.reason}</td>
              <td>
                {request.attachment ? (
                  <a href={`images/attachment/${request.attachment}`} target="_blank" rel="noopener noreferrer">
                    {t("View Attachment")}
                  </a>
                ) : (
                  t("No Attachment")
                )}
              </td>
              <td>
                <button className="btn btn-success me-2" onClick={() => handleApprove(request.id)}>
                  {t("Approve")}
                </button>
                <button className="btn btn-danger" onClick={() => handleReject(request.id)}>
                  {t("Reject")}
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default VerificationRequestsPage;
