import React from "react";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { logoutSucces } from "../redux/authActions";
import ProfileImageWithDefault from "../components/ProfileImageWithDefault";
import { useTranslation } from "react-i18next";
import logo from "../assets/logo.png";

const Sidebar = () => {
  const { displayName, isLoggedIn, image, username } = useSelector((store) => ({
    isLoggedIn: store.isLoggedIn,
    displayName: store.displayName,
    image: store.image,
    username: store.username,
  }));
  const { t } = useTranslation();
  const dispatch = useDispatch();

  const handleLogout = () => {
    dispatch(logoutSucces());
  };

  return (
    <div className="position-fixed top-2 start-0 bottom-2 bg-white text-" style={{ width: "250px" }}>
      {isLoggedIn && (
        <div className="card">
          <div className="card-body">
            <ul className="list-group">
              <Link className="list-group-item text-center" to="/">
                <img src={logo} width="48" alt="Hoaxify logo" />
              </Link>
              <br />
              <li className="list-group-item list-group-item-action">
                <Link
                  to="/"
                  className="d-flex align-items-center text-secondary"
                  style={{ textDecoration: "none" }}
                >
                  <i className="fas fa-home me-2"></i>
                  {t("Home")}
                </Link>
              </li>
              <li className="list-group-item list-group-item-action">
                <Link
                  to={"/users/" + username}
                  className="d-flex align-items-center text-dark"
                  style={{ textDecoration: "none" }}
                >
                  <ProfileImageWithDefault
                    image={image}
                    width="24"
                    height="24"
                    className="rounded-circle me-2"
                  />
                  <span>{displayName}</span>
                </Link>
              </li>
              <li className="list-group-item list-group-item-action">
                <Link
                  to={"/example-page"}
                  className="d-flex align-items-center text-dark"
                  style={{ textDecoration: "none" }}
                >
                  <i className="fas fa-clipboard me-2"></i>
                  {t("Example Page")}
                </Link>
              </li>
              <li className="list-group-item list-group-item-action" onClick={handleLogout}>
                <Link
                  to="/login"
                  className="d-flex align-items-center"
                  style={{ textDecoration: "none" }}
                >
                  <i className="fa-solid fa-right-from-bracket me-2"></i>
                  {t("Logout")}
                </Link>
              </li>
              <li className="list-group-item list-group-item-action">
                <Link
                  to={"/settings"}
                  className="d-flex align-items-center text-dark"
                  style={{ textDecoration: "none" }}
                >
                  <i className="fas fa-cog me-2"></i>
                  {t("Settings")}
                </Link>
              </li>
            </ul>
          </div>
        </div>
      )}
    </div>
  );
};

export default Sidebar;
