import React, { useEffect, useState } from "react";
import PostView from "../components/PostView";
import { fetchPostDetail } from "../api/apiCalls";
const PostViewDetails = (props) => {
  const postId = props.match.params.id; // ID parametresini al

  const [post, setPost] = useState({});
  const [newComment, setNewComment] = useState("");

  useEffect(() => {
    fetchPostDetail();
  }, []);

  const fetchPostDetail = async () => {
    try {
      const response = await axios.get(`/api/1.0/posts/${postId}`);
      setPost(response.data);
    } catch (error) {
      console.error("Error fetching post detail:", error);
    }
  };

  return (
    <div>
      <PostView post={post} />
      <div className="mt-3">
        <textarea
          rows="4"
          className="form-control"
          placeholder="Write a comment..."
          value={newComment}
          onChange={handleCommentChange}
        ></textarea>
        <div className="d-flex justify-content-end mt-2">
          <button className="btn btn-primary" onClick={postComment}>
            Post Comment
          </button>
        </div>
      </div>
      <div className="mt-4">
        {comments.map((comment) => (
          <div key={comment.id} className="card my-2">
            <div className="card-body">{comment.content}</div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PostViewDetails;
