import React from "react";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { logoutSuccess } from "../redux/authActions";
import ProfileImageWithDefault from "../components/ProfileImageWithDefault";

const Sidebar = () => {
  const { displayName, isLoggedIn, image, username } = useSelector((store) => ({
    isLoggedIn: store.isLoggedIn,
    displayName: store.displayName,
    image: store.image,
    username: store.username,
  }));

  const dispatch = useDispatch();

  const onLogoutSuccess = () => {
    dispatch(logoutSuccess()); // logoutSucces yerine logoutSuccess olarak düzeltildi.
  };

  return (
    <div className="col-lg-2 d-none d-lg-block position-fixed fixed-left">
      <div className="">
        <div className="card-body">
          <ul className="list-group">
            {/* ... (mevcut kodlarınız) ... */}
            {isLoggedIn && (
              <li className="list-group-item" onClick={onLogoutSuccess}>
                <Link
                  to="/login"
                  className="d-flex align-items-center"
                  style={{ textDecoration: "none" }}
                >
                  <i className="material-icons text-warning me-1">logout</i>
                  Logout
                </Link>
              </li>
            )}
            {/* Add more buttons for other functionalities */}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
