import React, { useEffect, useState } from "react";
import { getOldPosts, getPosts } from "../api/apiCalls";
import { useTranslation } from "react-i18next";
import PostView from "./PostView";
import { useApiProgress } from "../shared/ApiProgress";
import Spinner from "../components/Spinner";
import { useParams } from "react-router-dom";

const PostFeed = () => {
  const [postPage, setPostPage] = useState({
    content: [],
    last: true,
    number: 0,
  });

  const { t } = useTranslation();
  const { username } = useParams();
  const path = username ? `/api/1.0/users/${username}/posts?page=` : "/api/1.0/posts?page=";
  
  const pendingApiCall = useApiProgress("get", path);

  useEffect(() => {const loadPosts = async (page) => {
    try {
      const response = await getPosts(username,page);
      setPostPage((previousPostPage) => ({
        ...response.data,
        content: [...previousPostPage.content, ...response.data.content],
      }));
    } catch (error) {}
  };
    loadPosts();
  }, [username]);

  

  const loadOldPosts = async ()=>{
    const lastPostIndex = postPage.content.length - 1;
    const lastPostId = postPage.content[lastPostIndex].id;
    const response = await getOldPosts(lastPostId);
    setPostPage((previousPostPage) => ({
      ...response.data,
      content: [...previousPostPage.content, ...response.data.content],
    }));
  }


  const { content, last } = postPage;

  if (content.length === 0) {
    return (
      <div className="alert alert-secondary text-center">
        {t("There are no posts")}
      </div>
    );
  }

  return (
    <div>
      {content.map((post) => (
        <div key={post.id} className="mb-">
          <PostView post={post}></PostView>
        </div>
      ))}
      {!last && (
        <div
          className="alert alert-secondary text-center"
          onClick={pendingApiCall ? () => {} : () => loadOldPosts()}
          style={{ cursor: pendingApiCall ? "wait" : "pointer" }}
        >
          {pendingApiCall ? <Spinner /> : t("Load old posts")}
        </div>
      )}
    </div>
  );
};

export default PostFeed;
