import React, { useEffect, useState } from "react";
import { useTranslation } from "react-i18next";
import ProfileImageWithDefault from "./ProfileImageWithDefault";
import { useSelector } from "react-redux";
import { postIt } from "../api/apiCalls";
import { useApiProgress } from "../shared/ApiProgress";
import ButtonWithProgress from "../pages/ButtonWithProgress";
import EmojiPicker from "react-emoji-picker";

const PostSubmit = () => {
  const { t } = useTranslation();
  const { image } = useSelector((store) => ({ image: store.image }));
  const [focused, setFocused] = useState(false);
  const [post, setPost] = useState("");
  const [errors, setErrors] = useState({});
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);

  const onSelectEmoji = (emoji) => {
    setPost(post + emoji);
  };

  const toggleEmojiPicker = () => {
    setShowEmojiPicker((prevState) => !prevState);
  };

  useEffect(() => {
    if (!focused) {
      setPost("");
      setErrors({});
    }
  }, [focused]);

  useEffect(() => {
    setErrors({});
  }, [post]);

  const pendingApiCall = useApiProgress("post", "/api/1.0/posts");

  const onClickPostIt = async () => {
    const body = {
      content: post,
    };
    try {
      await postIt(body);
      setFocused(false);
    } catch (error) {
      if (error.response.data.validationErrors) {
        setErrors(error.response.data.validationErrors);
      }
    }
  };
  let textAreaClass = "mb-2 form-control";
  if (errors.content) {
    textAreaClass += " is-invalid";
  }

  return (
    <div className="card p-2">
      <div className="d-flex flex-wrap align-items-start">
        <div className="col-2 col-md-1">
          <ProfileImageWithDefault
            className="rounded-circle"
            image={image}
            width="32"
            height="32"
          />
        </div>
        <div className="col-10 col-md-11">
          <textarea
            className={textAreaClass}
            rows={focused ? "4" : "3"}
            onFocus={() => setFocused(true)}
            placeholder={t("What's happening?")}
            onChange={(event) => {
              setPost(event.target.value);
            }}
            value={post}
          ></textarea>

          <div className="invalid-feedback">{errors.content}</div>

          <div className="d-flex justify-content-end">
            {focused && (
              <ButtonWithProgress
                className="btn btn-info d-flex align-items-center"
                onClick={onClickPostIt}
                pendingApiCall={pendingApiCall}
                disabled={pendingApiCall}
                text={t(" TalkIt")}
                icon="fa-regular fa-paper-plane fa-beat me-2"
              >
                {/* <i className="material-icons me-1">send</i> */}
                {/* <span className="ml-1">{t(" TalkIt")}</span> */}
              </ButtonWithProgress>
            )}
            {focused && (
              <button
                className="btn btn-light d-inline-flex align-items-center ml-1"
                onClick={toggleEmojiPicker} // Toggle emoji picker when the emoji button is clicked
                disabled={false}
              >
                <i className="material-icons">emoji_emotions</i>
              </button>
            )}
            {focused && (
              <button
                className="btn btn-light d-inline-flex ml-1"
                onClick={() => setFocused(false)}
                disabled={pendingApiCall}
              >
                <i className="material-icons">close</i>
                {t("Cancel")}
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default PostSubmit;
