import React, { useState } from 'react';

const VerifiedBadge = () => {
  const [modalVisible, setModalVisible] = useState(false);

  const openModal = () => {
    setModalVisible(true);
  };

  const closeModal = () => {
    setModalVisible(false);
  };

  return (
    <>
      <span
        className="d-inline-block"
        tabIndex="0"
        onClick={openModal}
        style={{ cursor: 'pointer' }}
      >
        <i className="material-icons text-info align-middle ms-2">
          verified
        </i>
      </span>

      {modalVisible && (
        <div className="modal fade show d-block" tabIndex="-1" style={{ display: 'block' }}>
          <div className="modal-dialog modal-dialog-centered">
            <div className="modal-content">
              <div className="modal-body">
                <i className="material-icons text-info align-middle me-2">
                  verified
                </i>
                Modal içeriği buraya gelecek.
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-secondary" onClick={closeModal}>Kapat</button>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default VerifiedBadge;
